import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import "./SignForm.css";
import logoo from "./Assets/arrow.png";

const SignInForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation logic for the email field
    if (!email || !email.trim()) {
      setEmailError("Email is required.");
    } else if (!isValidEmail(email)) {
      setEmailError("Invalid email format.");
    } else {
      setEmailError("");
    }

    // Validation logic for the password field
    if (!password || password.length < 6) {
      setPasswordError("Password must be at least 6 characters.");
    } else {
      setPasswordError("");
    }

    // If both email and password are valid, you can proceed with the submission
    if (!emailError && !passwordError) {
      console.log("Email:", email);
      console.log("Password:", password);
      // Perform your submission logic here
    }
  };

  const isValidEmail = (email) => {
    // You can implement your email validation logic here
    // For a simple example, we check if the email contains "@" symbol
    return email.includes("@");
  };

  return (
    <div className="signn">
      <p>Sign In</p>
      <Form onSubmit={handleSubmit}>
        <div className="txt_field">
          <label>Email Address</label>
          <input type="email" required value={email} onChange={(e) => setEmail(e.target.value)} />
          <div className="error-message">{emailError}</div>
        </div>
        <div className="txt_field">
          <label>Password</label>
          <input type="password" required value={password} onChange={(e) => setPassword(e.target.value)} />
          <div className="error-message">{passwordError}</div>
        </div>
        <button type="submit" className="signin-button">SIGN IN
          <div className="logocontainer"><img src={logoo} width={20} height={20} /></div>
        </button>
      </Form>
    </div>
  );
};

export default SignInForm;
