import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import "./SignForm.css"
import logoo from "./Assets/arrow.png"

const SignInForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State to manage success message display

  const handleSignIn = () => {
    // Simulating login logic (replace this with your actual authentication logic)
    if (email === 'purushendrakarn@gmail.com' && password === 'password') {
      console.log("Login successful!");
      setLoggedIn(true);
      setShowSuccessMessage(true); // Display success message upon successful login
    } else {
      console.log("Invalid credentials");
      setLoggedIn(false);
      setShowSuccessMessage(true); // Hide success message for invalid credentials
    }
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div className="signn">
      <p>Sign In</p>
      <Form>
        <div className="txt_field">
          <label>Email Address</label>
          <input type="email" required onChange={handleEmailChange} />
        </div>
        <div className="txt_field">
          <label>Password</label>
          <input type="password" required onChange={handlePasswordChange} />
        </div>
        <button className="signin-button" onClick={handleSignIn}>
          {loggedIn ? 'Logged In' : 'SIGN IN'}
          <div className="logocontainer" ><img src={logoo} width={20} height={20} /></div>
        </button>
        {showSuccessMessage && (
          <p className="success-message">Login successful! Redirecting...</p>
        )}
      </Form>
    </div>
  );
};

export default SignInForm;
